import React from "react";

import ItemPortfolio from "../ItemPortfolio/ItemPortfolio";

function Portfolio() {
  const portfolios = [
    {
      link: "https://matheusmirandalacerda.github.io/nlw-esports-explorer/",
      image:
        "data:image/jpeg;base64,",
      title: "Projeto NLW eSports",
    },
    {
      link: "https://github.com/matheusmirandalacerda/-Projeto_Conceitual_Banco_de_Dados_E-Commerce/",
      image:
        "data:image/jpeg;base64,",
      title: "Projeto de Banco de Dados",
    },
    {
      link: "https://github.com/matheusmirandalacerda/Projeto_Covid/blob/main/projeto_covid.ipynb/",
      image:
        "data:image/png;base64,",
      title: "Utilizando Pandas"
    }
  ];

  return (
    <section>
      <h2>Portfólio</h2>

      {portfolios.map(
        (portfolio, index) => 
        <ItemPortfolio
          key={index}
          link={portfolio.link}
          image={portfolio.image}
          title={portfolio.title}
        ></ItemPortfolio>
      )}
    </section>
  )
}

export default Portfolio;

